mapper.select:
SELECT D001_ID AS KEY, (SELECT D002_MAINNM FROM HANGIL.TB_D002_ASSETCODE WHERE D002_MAINKEY = SUBSTR(D001_ASSCD,1,2) GROUP BY D002_MAINNM) || '_' || (SELECT D002_SUBNM FROM HANGIL.TB_D002_ASSETCODE WHERE D002_MAINKEY = SUBSTR(D001_ASSCD,1,2) AND D002_SUBKEY = SUBSTR(D001_ASSCD,3,2)) AS ASSNM, DECODE(D001_BUIL, 'M', '본관', 'N', '신관') AS BUIL, (SELECT D003_NAME FROM HANGIL.TB_D003_TEAM WHERE D001_TEAM = D003_KEY) AS TEAM, D001_FLOOR AS FLOOR, D001_PLACE AS PLACE, D001_USER AS USERNM, ( SELECT D004_OPTION FROM HANGIL.TB_D004_CODE WHERE D004_CODE = 'YEAR' AND D004_OPTION = D001_YEAR) AS YEAR, D001_IP AS IP, ( SELECT D004_OPTION FROM HANGIL.TB_D004_CODE WHERE D004_CODE = 'MS' AND D004_OPTION = D001_MS) AS MS, ( SELECT D004_OPTION FROM HANGIL.TB_D004_CODE WHERE D004_CODE = 'HWP' AND D004_OPTION = D001_HWP) AS HWP, D001_EYE AS EYE, D001_PACS AS PACS FROM HANGIL.TB_D001_STOCK WHERE 1=1
AND SUBSTR(D001_ASSCD,1,2) = #{assMainCd}
AND SUBSTR(D001_ASSCD,3,2) = #{assSubCd}
AND D001_BUIL = #{buil}
AND D001_FLOOR = #{floor}
AND D001_TEAM = #{team}
AND D001_USEYN = #{useYn}
ORDER BY D001_ID DESC

mapper.update:
UPDATE HANGIL.TB_D001_STOCK SET D001_USEYN = 'D',
D001_MEMO = #{memo},
D001_UPDTIME = SYSDATE WHERE D001_ID = #{id}

UPDATE HANGIL.TB_D001_STOCK SET D001_BUIL = '', D001_TEAM = '', D001_FLOOR = '', D001_PLACE = '', D001_USER = '', -- D001_YEAR = '', D001_IP = '', D001_MS = '', D001_HWP = '', D001_EYE = '', D001_PACS = '',
D001_MEMO = #{memo},
D001_USEYN = 'N', D001_UPDTIME = SYSDATE WHERE D001_ID = #{id}
